'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var quantile = require( './../lib' );

var i;
var m;
var n;
var k;
var p;
var y;

for ( i = 0; i < 10; i++ ) {
	p = randu();
	m = round( randu() * 20 );
	n = round( randu() * 20 );
	k = round( randu() * 10 );
	y = quantile( p, m, n, k );
	console.log( 'p: %d, m: %d, n: %d, k: %d, Q(p;m,n,k): %d',
		p.toFixed( 4 ), m, n, k, y.toFixed( 4 )
	);
}
