'use strict';

/**
* Hypergeometric distribution quantile function.
*
* @module @stdlib/math/base/dist/hypergeometric/quantile
*
* @example
* var quantile = require( '@stdlib/math/base/dist/hypergeometric/quantile' );
*
* var y = quantile( 0.4, 20, 20, 10 );
* // returns 5
*
* y = quantile( 0.8, 40, 20, 20 );
* // returns 15
*
* y = quantile( 0.5, 10, 90, 10 )
* // returns 1
*
* y = quantile( 0.0, 40, 60, 20 )
* // returns 0
*
* y = quantile( 1.0, 40, 60, 20 )
* // returns 20
*
* var myquantile = factory( 20, 80, 10 );
* y = myquantile( 0.2 );
* // returns 1
*
* y = myquantile( 0.9 );
* // returns 4
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var quantile = require( './quantile.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( quantile, 'factory', factory );


// EXPORTS //

module.exports = require( './quantile.js' );
