'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var factory = require( './../lib/factory.js' );


// FIXTURES //

var data = require( './fixtures/julia/data.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a function', function test( t ) {
	var quantile = factory( 4, 4, 2 );
	t.equal( typeof quantile, 'function', 'returns a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the created function returns `NaN`', function test( t ) {
	var quantile;
	var y;

	quantile = factory( 4, 4, 2 );
	y = quantile( NaN );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( NaN, 4, 2 );
	y = quantile( 0.2 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( 4, NaN, 2 );
	y = quantile( 0.2 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( 4, 4, NaN );
	y = quantile( 0.2 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( 4, NaN, NaN );
	y = quantile( 0.2  );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( NaN, 4, NaN );
	y = quantile( 0.2  );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( NaN, NaN, 2 );
	y = quantile( 0.2  );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( NaN, NaN, NaN );
	y = quantile( 0.2 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( NaN, NaN, NaN );
	y = quantile( NaN );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided valid parameters, the function returns a function which returns `NaN` when provided a number outside `[0,1]` for `p`', function test( t ) {
	var quantile;
	var y;

	quantile = factory( 4, 2, 2 );
	y = quantile( -0.1 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 1.1 );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a `m` which is not a non-negative integer, the created function always returns `NaN`', function test( t ) {
	var quantile;
	var y;

	quantile = factory( 20.5, 4, 2 );

	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( -1.0, 4, 2 );

	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( NINF, 4, 2 );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( PINF, 4, 2 );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a `n` which is not a non-negative integer, the created function always returns `NaN`', function test( t ) {
	var quantile;
	var y;

	quantile = factory( 20, 20.5, 2 );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( 4, -1.0, 2 );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( 4, NINF, 2 );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( 4, PINF, 2 );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a `k` which is not a non-negative integer, the created function always returns `NaN`', function test( t ) {
	var quantile;
	var y;

	quantile = factory( 20, 10, 2.5 );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( 4, 2, -1.0 );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( 4, 2, NINF );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	quantile = factory( 4, 2, PINF );
	y = quantile( 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'given valid parameters, the created function returns `max( 0, k - n )` as the 0% quantile', function test( t ) {
	var quantile;
	var y;

	quantile = factory( 10, 10, 5 );
	y = quantile( 0.0 );
	t.equal( y, 0, 'returns 0' );

	quantile = factory( 10, 10, 15 );
	y = quantile( 0.0 );
	t.equal( y, 5, 'returns 5' );
	t.end();
});

tape( 'given valid parameters, the created function returns `min( k, m )` as the 100% quantile', function test( t ) {
	var quantile;
	var y;

	quantile = factory( 20, 10, 15 );
	y = quantile( 1.0 );
	t.equal( y, 15, 'returns 15' );

	quantile = factory( 20, 10, 25 );
	y = quantile( 1.0 );
	t.equal( y, 20, 'returns 20' );

	quantile = factory( 20, 10, 5 );
	y = quantile( 1.0 );
	t.equal( y, 5, 'returns 5' );

	t.end();
});

tape( 'the created function evaluates the quantile for `p` given valid parameters', function test( t ) {
	var expected;
	var quantile;
	var p;
	var m;
	var n;
	var k;
	var y;
	var i;

	expected = data.expected;
	p = data.p;
	m = data.m;
	n = data.n;
	k = data.k;
	for ( i = 0; i < p.length; i++ ) {
		quantile = factory( m[i], n[i], k[i] );
		y = quantile( p[i] );
		t.equal( y, expected[i], 'p: '+p[i]+', m: '+m[i]+', n: '+n[i]+', k: '+k[i]+', y: '+y+', expected: '+expected[i] );
	}
	t.end();
});
