'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var quantile = require( './../lib' );


// FIXTURES //

var data = require( './fixtures/julia/data.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof quantile, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the function returns `NaN`', function test( t ) {
	var y = quantile( NaN, 20, 20, 10 );
	t.equal( isnan( y ), true, 'returns NaN' );
	y = quantile( 0.2, NaN, 10, 10  );
	t.equal( isnan( y ), true, 'returns NaN' );
	y = quantile( 0.2, 20, NaN, 10 );
	t.equal( isnan( y ), true, 'returns NaN' );
	y = quantile( 0.2, 20, 20, NaN );
	t.equal( isnan( y ), true, 'returns NaN' );
	t.end();
});

tape( 'if provided a number outside `[0,1]` for `p` and valid parameters, the function returns `NaN`', function test( t ) {
	var y = quantile( 1.1, 20, 10, 10 );
	t.equal( isnan( y ), true, 'returns true' );
	y = quantile( -0.1, 20, 10, 10 );
	t.equal( isnan( y ), true, 'returns true' );
	t.end();
});

tape( 'if provided a `m` which is not a non-negative integer, the function returns `NaN`', function test( t ) {
	var y;

	y = quantile( 0.5, 10.5, 10, 10 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 1.5, 10, 10 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, PINF, 10, 5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, NINF, 10, 5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, -2.0, 10, 5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, -0.5, 10, 5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a `n` which is not a non-negative integer, the function returns `NaN`', function test( t ) {
	var y;

	y = quantile( 0.5, 10, 10.5, 10 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 10, 1.5, 10 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 10, PINF, 5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 10, NINF, 5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 10, -2.0, 5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 10, -0.5, 5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a `k` which is not a non-negative integer, the function returns `NaN`', function test( t ) {
	var y;

	y = quantile( 0.5, 10, 10, 10.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 10, 10, 1.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 10, 10, PINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 10, 10, NINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 10, 5, -2.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 10, 5, -0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a success probability `p` outside `[0,1]`, the function returns `NaN`', function test( t ) {
	var y;

	y = quantile( 0.5, 20, 1.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 20, -1.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 20, NINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = quantile( 0.5, 20, PINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'given valid parameters, the function returns `max( 0, k - n )` as the 0% quantile', function test( t ) {
	var y = quantile( 0.0, 10, 10, 5 );
	t.equal( y, 0, 'returns 0' );

	y = quantile( 0.0, 10, 10, 15 );
	t.equal( y, 5, 'returns 5' );
	t.end();
});

tape( 'given valid parameters, the function returns `min( k, m )` as the 100% quantile', function test( t ) {
 	var y = quantile( 1.0, 20, 10, 15 );
	t.equal( y, 15, 'returns 15' );

	y = quantile( 1.0, 20, 10, 25 );
	t.equal( y, 20, 'returns 20' );

	y = quantile( 1.0, 20, 10, 5 );
	t.equal( y, 5, 'returns 5' );

	t.end();
});

tape( 'the function evaluates the quantile for `p` given valid parameters', function test( t ) {
	var expected;
	var p;
	var m;
	var n;
	var k;
	var y;
	var i;

	expected = data.expected;
	p = data.p;
	m = data.m;
	n = data.n;
	k = data.k;
	for ( i = 0; i < p.length; i++ ) {
		y = quantile( p[i], m[i], n[i], k[i] );
		t.equal( y, expected[i], 'p: '+p[i]+', m: '+m[i]+', n: '+n[i]+', k: '+k[i]+', y: '+y+', expected: '+expected[i] );
	}
	t.end();
});
