'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var randu = require( '@stdlib/math/base/random/randu' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var isProbability = require( '@stdlib/math/base/utils/is-probability' );
var EPS = require( '@stdlib/math/constants/float64-eps' );
var pkg = require( './../package.json' ).name;
var cdf = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var alpha;
	var beta;
	var x;
	var y;
	var i;

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		x = ( randu()*200.0 ) + EPS;
		alpha = ( randu()*100.0 ) + EPS;
		beta = ( randu()*100.0 ) + EPS;
		y = cdf( x, alpha, beta );
		if ( isnan( y ) ) {
			b.fail( 'should not return NaN' );
		}
		if ( !isProbability( y ) ) {
			b.fail( 'should not return a number outside `[0,1]`' );
		}
	}
	b.toc();
	if ( isnan( y ) ) {
		b.fail( 'should not return NaN' );
	}
	else if ( isProbability( y ) ) {
		b.pass( 'benchmark finished' );
	}
	else {
		b.fail( 'should not return a number outside `[0,1]`' );
	}
	b.end();
});

bench( pkg+':factory', function benchmark( b ) {
	var mycdf;
	var alpha;
	var beta;
	var x;
	var y;
	var i;

	alpha = 1.5;
	beta = 2.3;
	mycdf = cdf.factory( alpha, beta );

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		x = ( randu()*10.0 );
		y = mycdf( x );
		if ( isnan( y ) ) {
			b.fail( 'should not return NaN' );
		}
		if ( !isProbability( y ) ) {
			b.fail( 'should not return a number outside `[0,1]`' );
		}
	}
	b.toc();
	if ( isnan( y ) ) {
		b.fail( 'should not return NaN' );
	}
	else if ( isProbability( y ) ) {
		b.pass( 'benchmark finished' );
	}
	else {
		b.fail( 'should not return a number outside `[0,1]`' );
	}
	b.end();
});

