#!/usr/bin/env julia

import Distributions: mgf, Laplace
import JSON

"""
	gen( x, mu, b, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `mu`: location parameter
* `b`: scale parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = rand( 1000 ) * 15;
julia> mu = rand( 1000 ) * 10.0;
julia> b = rand( 1000 ) * 5.0;
julia> gen( x, mu, b, "data.json" );
```
"""
function gen( x, mu, b, name )
	z = Array( Float64, length(x) );
	for i in eachindex(x)
		z[ i ] = mgf( Laplace( mu[i], b[i] ), x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("mu", mu),
		("b", b),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Negative mean:
mu = rand( 1000 ) * -10.0;
b = rand( 1000 ) * 5.0;
x = -1.0 ./ b + ( 2.0 ./ b ) .* rand( 1000 );
gen( x, mu, b, "negative_mean.json" );

# Positive mean:
mu = rand( 1000 ) * 10.0;
b = rand( 1000 ) * 5.0;
x = -1.0 ./ b + ( 2.0 ./ b ) .* rand( 1000 );
gen( x, mu, b, "positive_mean.json" );

# Large variance:
mu = rand( 1000 );
b = rand( 1000 ) * 20.0;
x = -1.0 ./ b + (2.0 ./ b) .* rand( 1000 );
gen( x, mu, b, "large_variance.json" );
