'use strict';

// FUNCTIONS //

var abs = require( '@stdlib/math/base/special/abs' );
var exp = require( '@stdlib/math/base/special/exp' );
var ln = require( '@stdlib/math/base/special/ln' );


// PDF //

/**
* Evaluates the probability density function (PDF) for a Laplace distribution with location parameter `mu` and scale parameter `b` at a value `x`.
*
* @param {number} x - input value
* @param {number} mu - location parameter
* @param {PositiveNumber} b - scale parameter
* @returns {number} evaluated PDF
*
* @example
* var y = pdf( 2.0, 0.0, 1.0 );
* // returns ~0.068
* @example
* var y = pdf( -1.0, 2.0, 3.0 );
* // returns ~0.061
* @example
* var y = pdf( 2.5, 2.0, 3.0 );
* // returns ~0.141
* @example
* var y = pdf( NaN, 0.0, 1.0 );
* // returns NaN
* @example
* var y = pdf( 0.0, NaN, 1.0 );
* // returns NaN
* @example
* var y = pdf( 0.0, 0.0, NaN );
* // returns NaN
* @example
* // Negative scale parameter:
* var y = pdf( 2.0, 0.0, -1.0 );
* // returns NaN
*/
function pdf( x, mu, b ) {
	var lnb = ln( 2 * b );
	var logd = - lnb - abs( x - mu ) / b;
	return exp( logd );
} // end FUNCTION pdf()


// EXPORTS //

module.exports = pdf;
