'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );


// MAIN //

/**
* Returns the mode for a LÃ©vy distribution with location `mu` and scale `c`.
*
* @param {number} mu - location parameter
* @param {PositiveNumber} c - scale parameter
* @returns {number} mode
*
* @example
* var y = mean( 0.0, 1.0 );
* // returns ~0.333
* @example
* var y = mean( 5.0, 2.0 );
* // returns ~5.667
* @example
* var y = mean( NaN, 1.0 );
* // returns NaN
* @example
* var y = mean( 0.0, NaN );
* // returns NaN
* @example
* var y = mean( 0.0, 0.0 );
* // returns NaN
*/
function mean( mu, c ) {
	if (
		isnan( mu ) ||
		isnan( c ) ||
		c <= 0.0
	) {
		return NaN;
	}
	return mu + ( c / 3.0 );
} // end FUNCTION mean()


// EXPORTS //

module.exports = mean;
