'use strict';

// FUNCTIONS //

var exp = require( '@stdlib/math/base/special/exp' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );


// CDF //

/**
* Evaluates the cumulative distribution function (CDF) for a logistic distribution with location parameter `mu` and scale parameter `s` at a value `x`.
*
* @param {number} x - input value
* @param {number} mu - location parameter
* @param {NonNegativeNumber} s - scale parameter
* @returns {Probability} evaluated CDF
*
** @example
* var y = cdf( 2.0, 0.0, 1.0 );
* // returns ~0.881
* * @example
* var y = cdf( 5.0, 10.0, 3.0 );
* // returns ~0.159
* * @example
* var y = cdf( 2.0, 0.0, NaN );
* // returns NaN
* @example
* var y = cdf( 2.0, 0.0, NaN );
* // returns NaN
* @example
* var y = cdf( 2, NaN, 1.0 );
* // returns NaN
* @example
* var y = cdf( NaN, 0.0, 1.0 );
* // returns NaN
*/
function cdf( x, mu, s ) {
	if ( isnan( x ) || isnan( mu ) || isnan( s ) ) {
		return NaN;
	}
	if ( s < 0.0 ) {
		return NaN;
	}
	if ( s === 0.0 ) {
		return (x < mu) ? 0.0 : 1.0;
	}
	var z = ( x - mu ) / s;
	return 1 / ( 1 + exp( -z ) );
} // end FUNCTION cdf()


// EXPORTS //

module.exports = cdf;
