'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var getMean = require( './../lib' );

var mean;
var i;
var r;
var p;

for ( i = 0; i < 10; i++ ) {
	r = randu() * 100;
	p = randu();
	mean = getMean( r, p );
	console.log( 'r: %d, p: %d, E(X;r,p): %d',
		r,
		p.toFixed( 4 ),
		mean.toFixed( 4 )
	);
}

