#!/usr/bin/env julia

import Distributions: var, NegativeBinomial
import JSON

"""
	gen( r, p, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `r`: number of failures until experiment is stopped
* `p`: success probability
* `name::AbstractString`: output filename

# Examples

``` julia
julia> r = round( rand( 1000 ) * 20 );
julia> p = rand( 1000 );
julia> gen( r, p, \"data.json\" );
```
"""
function gen( r, p, name )
	z = Array( Float64, length(p) );
	for i in eachindex(p)
		z[ i ] = var( NegativeBinomial( r[ i ], p[ i ] ) );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("r", r),
		("p", p),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Generate fixtures:
r = round( Int, rand( 200 ) * 100.0 + 1.0 );
p = rand( 200 );
gen( r, p, "data.json" );

