'use strict';

var cdf = require( './../lib' );

var sigma;
var mu;
var x;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	x = Math.random() * 10;
	mu = Math.random() * 10 - 5;
	sigma = Math.random() * 20;
	y = cdf( x, mu, sigma );
	console.log( 'x: %d, µ: %d, σ: %d, f(x;µ,σ): %d', x, mu, sigma, y );
}
