# To run this script, `cd` to the `./test/fixtures` directory and then, from the R shell, `source("./runner.R")`.
options( digits = 16 )
library( jsonlite )

set.seed( 200 )
mu = sample( seq( -10, 10, length.out = 1000 ) )
sigma = sample( seq( 0.1, 100, length.out = 1000 )  )
x = sample( seq( -100, 100, length.out = 1000 ) )
y = pnorm( x, mu, sigma )

data = list(
	mu = mu,
	sigma = sigma,
	x = x,
	expected = y
)

write( toJSON( data, digits = 16, auto_unbox = TRUE ), "./data.json" )
