'use strict';

var pdf = require( './../lib' );

var x;
var mu;
var sigma;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	x = Math.random() * 10;
	mu = Math.random() * 10 - 5;
	sigma = Math.random() * 20;
	v = pdf( x, mu, sigma );
	console.log( 'x: %d, mu: %d, sigma: %d, f(x;mu,sigma): %d', x, mu, sigma, v );
}
