'use strict';

// MODULES //

var exp = require( '@stdlib/math/base/special/exp' );
var pow = require( '@stdlib/math/base/special/pow' );
var sqrt = require( '@stdlib/math/base/special/sqrt' );
var PI = require( '@stdlib/math/constants/float64-pi' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );


// FACTORY //

/**
* FUNCTION: factory( mu, sigma )
*	Partially applies mean  `mu` and standard deviation `sigma` and returns a function for evaluating the probability density function (PDF) for a Normal distribution.
*
* @param {Number} mu - mean
* @param {Number} sigma - standard deviation
* @returns {Function} PDF
*/
function factory( mu, sigma ) {
	var s2 = pow( sigma, 2 );
	var A = 1 / ( sqrt( 2 * s2 * PI ) );
	var B = -1 / ( 2 * s2 );
	/**
	* FUNCTION: pdf( x )
	*	Evaluates the probability density function (PDF) for a Normal distribution.
	*
	* @private
	* @param {Number} x - input value
	* @returns {Number} evaluated PDF
	*/
	if ( sigma < 0 ) {
		return function pdf() {
			return NaN;
		};
	}
	if ( sigma === 0 ) {
		return function pdf( x ) {
			return x === mu ? PINF : 0;
		};
	}
	return function pdf( x ) {
		return A * exp( B * pow( x - mu, 2 ) );
	};
} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
