'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var abs = require( '@stdlib/math/base/special/abs' );
var factory = require( './../lib/factory.js' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );


// FIXTURES //

var data = require( './fixtures/data.json' );


// FUNCTIONS //

function almostEqual( a, b, tol ) {
	var delta = abs( a - b );
	tol = tol * Math.max( 1, abs( a ), abs( b ) );
	return ( delta <= tol );
}


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a function', function test( t ) {
	var pdf = factory( 0, 1 );
	t.equal( typeof pdf, 'function', 'returns a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the created function returns `NaN`', function test( t ) {
	var pdf = factory( 0, 1 );
	var y = pdf( NaN );
	t.equal( isnan( y ), true, 'returns NaN' );
	pdf = factory( NaN, 1 );
	y = pdf( 0 );
	t.equal( isnan( y ), true, 'returns NaN' );
	pdf = factory( 1, NaN );
	y = pdf( 0 );
	t.equal( isnan( y ), true, 'returns NaN' );
	t.end();
});

tape( 'if provided  a negative standard deviation `sigma`, the created function returns `NaN`', function test( t ) {
	var pdf = factory( 0, -1 );
	var y = pdf( 2 );
	t.equal( isnan( y ), true, 'returns NaN' );
	t.end();
});

tape( 'if provided `sigma=0`, the created function evaluates a degenerate distribution centered at `mu`', function test( t ) {
	var pdf = factory( 2, 0 );
	var y = pdf( 2 );
	t.equal( y, PINF, 'returns PINF for x equal to mu' );
	y = pdf( 1 );
	t.equal( y, 0, 'returns 0 for any other x' );
	t.end();
});

tape( 'the created function evaluates the pdf for `x` given parameters `mu` and `sigma`', function test( t ) {
	var expected;
	var pdf;
	var bool;
	var tol;
	var x;
	var mu;
	var sigma;
	var y;
	var i;

	tol = 2e-16;

	expected = data.expected;
	x = data.x;
	mu = data.mu;
	sigma = data.sigma;
	for ( i = 0; i < x.length; i++ ) {
		pdf = factory( mu[i], sigma[i] );
		y = pdf( x[i] );
		if ( y === expected[i] ) {
			t.equal( y, expected[i], 'x: '+x[i]+', y: '+y+', expected: '+expected[i] );
		}  else {
			bool = almostEqual( y, expected[i], tol );
			t.ok( bool, 'within tolerance. x: '+x[i]+'. mu:'+mu[i]+'. sigma: '+sigma[i]+'. y: '+y+'. Expected: '+expected[i]+'. Tolerance: '+tol+'.' );
		}
	}
	t.end();
});
