'use strict';

var quantile = require( './../lib' );

var sigma;
var mu;
var p;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	p = Math.random();
	mu = Math.random() * 10 - 5;
	sigma = Math.random() * 20;
	y = quantile( p, mu, sigma );
	console.log( 'p: %d, µ: %d, σ: %d, Q(p;µ,σ): %d', p, mu, sigma, y );
}
