# To run this script, `cd` to the `./test/fixtures` directory and then, from the R shell, `source("./runner.R")`.
options( digits = 16 )
library( jsonlite )

set.seed( 878 )
mu = sample( seq( -10, 10, 0.5 ), size = 1000, replace = TRUE )
sigma = sample( seq( 0.1, 100, 0.1 ), size = 1000, replace = TRUE )
x = sample( seq( 0, 1, length.out = 1000 ) )
y = qnorm( x, mu, sigma )

data = list(
	mu = mu,
	sigma = sigma,
	x = x,
	expected = y
)

write( toJSON( data, digits = 16, auto_unbox = TRUE ), "./data.json" )
