'use strict';

// FUNCTIONS //


// PDF //

/**

*	Evaluates the probability density function (PDF) for a Pareto distribution with shape parameter `alpha` and scale parameter `beta` at a value `x`.
*
* @param {Number} x - input value
* @param {Number} alpha - shape parameter
* @param {Number} beta - scale parameter
* @returns {Number} evaluated PDF
*/
function pdf( x, alpha, beta ) {
	var num, denom;
	if ( x >= beta ) {
		num = alpha * require( '@stdlib/math/base/special/pow' )( beta, alpha );
		denom = require( '@stdlib/math/base/special/pow' )( x, alpha + 1 );
		return num / denom;
	} else {
		return 0;
	}
} // end FUNCTION pdf()


// EXPORTS //

module.exports = pdf;
