#!/usr/bin/env julia

import Distributions: var, Pareto
import JSON

"""
	gen( alpha, beta, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `alpha`: shape parameter
* `beta`: scale parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> alpha = rand( 1000 ) * 10.0;
julia> beta = rand( 1000 ) * 10.0;
julia> gen( alpha, beta, \"data.json\" );
```
"""
function gen( alpha, beta, name )
	z = Array( Float64, length(alpha) );
	for i in eachindex(alpha)
		z[ i ] = var( Pareto( alpha[i], beta[i] ) );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("alpha", alpha),
		("beta", beta),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Generate fixtures:
alpha = rand( 1000 ) * 10.0 + 10.0;
beta = rand( 1000 ) * 10.0 + 10.0;
gen( alpha, beta, "data.json" );

