'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/utils/is-nan' );


// MAIN //

/**
* Returns the expected value of a Poisson distribution.
*
* @param {NonNegativeNumber} lambda - mean parameter
* @returns {NonNegativeNumber} expected value
*
* @example
* var mean = getMean( 9 );
* // returns 9
* @example
* var mean = getMean( 1 );
* // returns 1
* @example
* var mean = getMean( -0.2 );
* // returns NaN
* @example
* var mean = getMean( NaN );
* // returns NaN
*/
function getMean( lambda ) {
	if ( isnan( lambda ) || lambda < 0.0 ) {
		return NaN;
	}
	return lambda;
} // end FUNCTION getMean()


// EXPORTS //

module.exports = getMean;
