#!/usr/bin/env julia

import Distributions: var, Poisson
import JSON

"""
	gen( lambda, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `lambda`: mean parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> lambda = rand( 1000 );
julia> gen( lambda, \"data.json\" );
```
"""
function gen( lambda, name )
	z = Array( Float64, length(lambda) );
	for i in eachindex(lambda)
		z[ i ] = sqrt( var( Poisson( lambda[ i ] ) ) );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("lambda", lambda),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Generate fixtures:
lambda = rand( 50 ) * 20.0;
gen( lambda, "data.json" );

