#!/usr/bin/env julia

import Distributions: entropy, Rayleigh
import JSON

"""
	gen( sigma, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `sigma`: scale parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> sigma = rand( 1000 );
julia> gen( sigma, \"data.json\" );
```
"""
function gen( sigma, name )
	z = Array( Float64, length(sigma) );
	for i in eachindex(sigma)
		z[ i ] = entropy( Rayleigh( sigma[ i ] ) );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("sigma", sigma),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Generate fixtures:
sigma = rand( 50 ) * 20.0 + 3.0;
gen( sigma, "data.json" );

