#!/usr/bin/env julia

import Distributions: logcdf, Rayleigh
import JSON

"""
	gen( x, sigma, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `sigma`: scale parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = linspace( 0, 50, 1001 );
julia> sigma = linspace( 1, 10, 1001 );
julia> gen( x, sigma, \"data.json\" );
```
"""
function gen( x, sigma, name )
	z = Array( Float64, length(x) );
	for i in eachindex(x)
		z[ i ] = logcdf( Rayleigh( sigma[i] ), x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("sigma", sigma),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Large scale parameter:
x = rand( 1000 ) * 20.0;
sigma = rand( 1000 ) * 50.0;
gen( x, sigma, "large_scale.json" );

# Medium scale parameter:
x = rand( 1000 ) * 20.0;
sigma = rand( 1000 ) * 20.0;
gen( x, sigma, "medium_scale.json" );

# Small scale parameter:
x = rand( 1000 ) * 20.0;
sigma = rand( 1000 ) * 5.0;
gen( x, sigma, "small_scale.json" );
