'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/utils/is-nan' );
var beta = require( '@stdlib/math/base/special/beta' );
var sqrt = require( '@stdlib/math/base/special/sqrt' );
var pow = require( '@stdlib/math/base/special/pow' );


// PDF //

/**
* Evaluates the probability density function (PDF) for a Student t distribution with degrees of freedom `v` at a value `x`.
*
* @param {number} x - input value
* @param {PositiveNumber} v - degrees of freedom
* @returns {number} evaluated PDF
*
* @example
* var y = pdf( 0.3, 4.0 );
* // returns ~0.355
* @example
* var y = pdf( 2.0, 0.7 );
* // returns ~0.058
* @example
* var y = pdf( -1.0, 0.5 );
* // returns ~0.139
* @example
* var y = pdf( 0.0, NaN );
* // returns NaN
* @example
* var y = pdf( NaN, 2.0 );
* // returns NaN
* @example
* var y = pdf( 2.0, -1.0 );
* // returns NaN
*/
function pdf( x, v ) {
	if (
		isnan( x ) ||
		isnan( v ) ||
		v <= 0.0
	) {
		return NaN;
	}
	return pow( v / ( v+pow(x,2.0) ), ( 1.0+v ) / 2.0 ) /
		( sqrt( v ) * beta( v/2.0, 0.5 ) );
} // end FUNCTION pdf()


// EXPORTS //

module.exports = pdf;
