#!/usr/bin/env julia

using Distributions
import JSON

"""
	gen( x, a, b, c, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `a`: minimum support
* `b`: maximum support
* `c`: mode
* `name::AbstractString`: output filename

"""
function gen( x, a, b, c, name )
	z = Array( Float64, length(x) );
	for i in eachindex(x)
		z[ i ] = pdf( TriangularDist( a[i], b[i], c[i] ), x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("a", a),
		("b", b),
		("c", c),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Small Range:
a = rand( 1000 ) * 20.0;
b = rand( 1000 ) * 20.0 + a;
c = a + ( b - a ) .* rand( 1000 );
x = a + ( b - a ) .* rand( 1000 );
gen( x, a, b, c, "small_range.json" );

# Medium Range:
a = rand( 1000 ) * 20.0;
b = rand( 1000 ) * 40.0 + a;
c = a + ( b - a ) .* rand( 1000 );
x = a + ( b - a ) .* rand( 1000 );
gen( x, a, b, c, "medium_range.json" );

# Large Range:
a = rand( 1000 ) * 20.0;
b = rand( 1000 ) * 80.0 + a;
c = a + ( b - a ) .* rand( 1000 );
x = a + ( b - a ) .* rand( 1000 );
gen( x, a, b, c, "large_range.json" );
