'use strict';

// MODULES //

var tape = require( 'tape' );
var getKeys = require( 'object-keys' ).shim();
var truncatedNormal = require( './../lib' );


// TESTS //

tape( 'main export is an object', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof truncatedNormal, 'object', 'main export is an object' );
	t.end();
});

tape( 'the exported object contains truncated normal distribution functions', function test( t ) {
	var keys = getKeys( truncatedNormal );
	t.equal( keys.length > 0, true, 'has keys' );
	t.end();
});
