#!/usr/bin/env julia

using Distributions
import JSON

"""
	gen( x, lambda, k, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `lambda`: shape parameter
* `k`: scale parameter
* `name::AbstractString`: output filename

"""
function gen( x, lambda, k, name )
	z = Array( Float64, length(x) );
	for i in eachindex(x)
		z[ i ] = cdf( Weibull( k[i], lambda[i] ), x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("lambda", lambda),
		("k", k),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Large scale parameter:
x = rand( 1000 ) * 5.0;
lambda = rand( 1000 ) * 10.0;
k = rand( 1000 ) * 10.0 + 10.0;
gen( x, lambda, k, "large_scale.json" );

# Large shape parameter:
x = rand( 1000 ) * 5.0;
lambda = rand( 1000 ) * 10.0;
k = rand( 1000 ) * 10.0 + 10.0;
gen( x, lambda, k, "large_shape.json" );

# Both large:
x = rand( 1000 ) * 5.0;
lambda = rand( 1000 ) * 20.0 + 10.0;
k = rand( 1000 ) * 20.0 + 10.0;
gen( x, lambda, k, "both_large.json" );
