#!/usr/bin/env julia

import Distributions: quantile, Beta
import JSON

"""
	gen( p, alpha, beta, name )

Generate fixture data and write to file.

# Arguments

* `p`: input value
* `alpha`: first shape parameter
* `beta`: second shape parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> p = rand( 1000 );
julia> alpha = rand( 1000 ) * 10.0;
julia> beta = rand( 1000 ) * 10.0;
julia> gen( p, alpha, beta, \"data.json\" );
```
"""
function gen( p, alpha, beta, name )
	z = Array( Float64, length(p) );
	for i in eachindex(p)
		z[ i ] = quantile( Beta( alpha[i], beta[i] ), p[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("p", p),
		("alpha", alpha),
		("beta", beta),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Large alpha parameter:
p = rand( 1000 );
alpha = rand( 1000 )*10.0 + 10.0;
beta = rand( 1000 )*10.0 + 2.0 ;
gen( p, alpha, beta, "large_alpha.json" );

# Large beta parameter:
p = rand( 1000 );
alpha = rand( 1000 )*10.0 + 2.0;
beta = rand( 1000 )*10.0 + 10.0;
gen( p, alpha, beta, "large_beta.json" );

# Both large:
p = rand( 1000 );
alpha = rand( 1000 )*20.0 + 10.0;
beta = rand( 1000 )*20.0 + 10.0;
gen( p, alpha, beta, "both_large.json" );
