'use strict';

// MAIN //

/**
* Returns the expected value of a beta prime distribution.
*
* @param {PositiveNumber} alpha - first shape parameter
* @param {PositiveNumber} beta - second shape parameter
* @returns {PositiveNumber} expected value
*
* @example
* var v = mean( 1.0, 2.0 );
* // returns 1.0
* @example
* var v = mean( 4.0, 12.0 );
* // returns ~0.364
* @example
* var v = mean( 8.0, 2.0 );
* // returns 8.0
* @example
* var v = mean( 1.0, 1.0 );
* // returns NaN
* @example
* var v = mean( 1.0, -0.1 );
* // returns NaN
* @example
* var v = mean( -0.1, 1.0 );
* // returns NaN
* @example
* var v = mean( 2.0, NaN );
* // returns NaN
* @example
* var v = mean( NaN, 2.0 );
* // returns NaN
*/
function mean( alpha, beta ) {
	if ( alpha <= 0.0 || beta <= 1.0 ) {
		return NaN;
	}
	return alpha / ( beta - 1.0 );
}


// EXPORTS //

module.exports = mean;
