'use strict';

// MAIN //

/**
* Returns the mode of a beta prime distribution.
*
* @param {PositiveNumber} alpha - first shape parameter
* @param {PositiveNumber} beta - second shape parameter
* @returns {PositiveNumber} mode
*
* @example
* var v = mode( 1.0, 2.0 );
* // returns 0.0
* @example
* var v = mode( 4.0, 12.0 );
* // returns ~0.231
* @example
* var v = mode( 8.0, 2.0 );
* // returns ~2.333
* @example
* var v = mode( 1.0, 1.0 );
* // returns NaN
* @example
* var v = mode( 1.0, -0.1 );
* // returns NaN
* @example
* var v = mode( -0.1, 1.0 );
* // returns NaN
* @example
* var v = mode( 2.0, NaN );
* // returns NaN
* @example
* var v = mode( NaN, 2.0 );
* // returns NaN
*/
function mode( alpha, beta ) {
	if ( alpha <= 0.0 || beta <= 0.0 ) {
		return NaN;
	}
	if ( alpha < 1.0 ) {
		return 0.0;
	}
	return ( alpha - 1.0 ) / ( beta + 1.0 );
} // end FUNCTION mode()


// EXPORTS //

module.exports = mode;
