'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var mgf = require( './../lib' );

var n;
var p;
var t;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	t = round( randu() * 5.0 );
	n = round( randu() * 10.0 );
	p = randu();
	y = mgf( t, n, p );
	console.log( 't: %d, n: %d, p: %d, M_X(t;n,p): %d', t, n, p.toFixed( 4 ), y.toFixed( 4 ) );
}
