'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var quantile = require( './../lib' );

var r;
var i;
var n;
var p;
var y;

for ( i = 0; i < 10; i++ ) {
	r = randu();
	n = round( randu() * 100.0 );
	p = randu();
	y = quantile( r, n, p );
	console.log( 'r: %d, n: %d, p: %d, Q(r;n,p): %d', r.toFixed( 4 ), n, p.toFixed( 4 ), y );
}
