'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var stdev = require( './../lib' );

var v;
var i;
var n;
var p;

for ( i = 0; i < 10; i++ ) {
	n = round( randu() * 100.0 );
	p = randu();
	v = stdev( n, p );
	console.log( 'n: %d, p: %d, SD(X;n,p): %d', n, p.toFixed( 4 ), v.toFixed( 4 ) );
}

