'use strict';

var randu = require( '@stdlib/random/base/randu' );
var EPS = require( '@stdlib/constants/math/float64-eps' );
var entropy = require( './../lib' );

var gamma;
var x0;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	x0 = randu() * 100.0;
	gamma = ( randu()*10.0 ) + EPS;
	v = entropy( x0, gamma );
	console.log( 'x0: %d, Î³: %d, h(X;x0,Î³): %d', x0.toFixed( 4 ), gamma.toFixed( 4 ), v.toFixed( 4 ) );
}

