'use strict';

var randu = require( '@stdlib/random/base/randu' );
var EPS = require( '@stdlib/constants/math/float64-eps' );
var logpdf = require( './../lib' );

var gamma;
var x0;
var x;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	x = randu() * 10.0;
	x0 = ( randu()*10.0 ) - 5.0;
	gamma = ( randu()*20.0 ) + EPS;
	y = logpdf( x, gamma, x0 );
	console.log( 'x: %d, x0: %d, Î³: %d, ln(f(x;x0,Î³)): %d', x.toFixed(4), x0.toFixed(4), gamma.toFixed(4), y.toFixed(4) );
}
