var v;
var i;

for ( i = 0; i < 10; i++ ) {
	x0 = randu() * 100.0;
	gamma = ( randu()*10.0 ) + EPS;
	v = mode( x0, gamma );
	console.log( 'x0: %d, Î³: %d, mode(X;x0,Î³): %d', x0.toFixed( 4 ), gamma.toFixed( 4 ), v.toFixed( 4 ) );
}

