'use strict';

var Chi = require( './../lib' );

var chi = new Chi( 2.0 );

var mu = chi.mean;
console.log( 'Mean = %d', mu );
// => Mean = 1.2533141373155003

var mode = chi.mode;
console.log( 'Mode = %d', mode );
// => Mode = 1.0

var s2 = chi.variance;
console.log( 'Variance = %d', s2 );
// => Variance = 0.429

var y = chi.cdf( 0.8 );
console.log( 'F(0.8) = %d', y );
// => F(0.8) = 0.274

