'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );
var sqrt = require( '@stdlib/math/base/special/sqrt' );


// MAIN //

/**
* Returns the mode of a chi distribution.
*
* @param {PositiveNumber} k - degrees of freedom
* @returns {NonNegativeNumber} mode
*
* @example
* var v = mode( 9.0 );
* // returns ~2.828
* @example
* var v = mode( 1.0 );
* // returns 0.0
* @example
* var v = mode( -0.2 );
* // returns NaN
* @example
* var v = mode( NaN );
* // returns NaN
*/
function mode( k ) {
	if ( isnan( k ) || k < 1.0 ) {
		return NaN;
	}
	return sqrt( k - 1.0 );
}


// EXPORTS //

module.exports = mode;
