'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );
var mean = require( '@stdlib/math/base/dists/chi/mean' );


// MAIN //

/**
* Returns the variance of a chi distribution.
*
* @param {NonNegativeNumber} k - degrees of freedom
* @returns {NonNegativeNumber} variance
*
* @example
* var v = variance( 9.0 );
* // returns ~0.485
* @example
* var v = variance( 1.0 );
* // returns ~0.363
* @example
* var v = variance( -0.2 );
* // returns NaN
* @example
* var v = variance( NaN );
* // returns NaN
*/
function variance( k ) {
	var mu;
	if ( isnan( k ) || k < 0.0 ) {
		return NaN;
	}
	mu = mean( k );
	return k - ( mu*mu );
}


// EXPORTS //

module.exports = variance;
