'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var cdf = require( './../lib' );

var k;
var x;
var y;
var i;

for ( i = 0; i < 20; i++ ) {
	x = randu() * 10.0;
	k = round( randu() * 10.0 );
	y = cdf( x, k );
	console.log( 'x: %d, k: %d, F(x;k): %d', x.toFixed( 4 ), k.toFixed( 4 ), y.toFixed( 4 ) );
}
