'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var pdf = require( './../lib' );

var k;
var x;
var y;
var i;

for ( i = 0; i < 20; i++ ) {
	x = randu() * 10.0;
	k = randu() * 10.0;
	y = pdf( x, k );
	console.log( 'x: %d, k: %d, f(x;k): %d', x.toFixed( 4 ), k.toFixed( 4 ), y.toFixed( 4 ) );
}
