'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var k;
var p;
var y;
var i;

for ( i = 0; i < 20; i++ ) {
	p = randu();
	k = randu() * 10.0;
	y = quantile( p, k );
	console.log( 'p: %d, k: %d, Q(p;k): %d', p.toFixed( 4 ), k.toFixed( 4 ), y.toFixed( 4 ) );
}
