'use strict';

var Cosine = require( './../lib' );

var cosine = new Cosine( 2.0, 4.0 );

var mean = cosine.mean;
console.log( 'Mean = %d', mean );
// => Mean = 2.0

var median = cosine.median;
console.log( 'Median = %d', median );
// => Median = 2.0

var s2 = cosine.variance;
console.log( 'Variance = %d', s2 );
// => Variance = 2.0910554567785242

var y = cosine.cdf( 0.8 );
console.log( 'F(0.8) = %d', y );
// => F(0.8) = 0.221240946299879

