'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var logpmf = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof logpmf, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the function returns `NaN`', function test( t ) {
	var y;

	y = logpmf( NaN, 0.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = logpmf( 0.0, NaN );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = logpmf( NaN, NaN );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns `0.0` if provided `x` equal to `mu`', function test( t ) {
	var y;

	y = logpmf( 2.0, 2.0 );
	t.equal( y, 0.0, 'returns 0.0' );

	y = logpmf( 0.0, 0.0 );
	t.equal( y, 0.0, 'returns 0.0' );

	y = logpmf( -3.0, -3.0 );
	t.equal( y, 0.0, 'returns 0.0' );

	t.end();
});

tape( 'the function returns `-Infinity` if provided `x` not equal to `mu`', function test( t ) {
	var y;

	y = logpmf( 2.0, 3.0 );
	t.equal( y, NINF, 'returns -Infinity' );

	y = logpmf( 4.0, 3.0 );
	t.equal( y, NINF, 'returns -Infinity' );

	t.end();
});
