'use strict';

// MODULES //

var tape = require( 'tape' );
var getKeys = require( 'object-keys' ).shim();
var erlang = require( './../lib' );


// TESTS //

tape( 'main export is an object', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof erlang, 'object', 'main export is an object' );
	t.end();
});

tape( 'the exported object contains Erlang distribution functions', function test( t ) {
	var keys = getKeys( erlang );
	t.equal( keys.length > 0, true, 'has keys' );
	t.end();
});
