#!/usr/bin/env julia

import Distributions: quantile, Frechet
import JSON

"""
	gen( p, alpha, s, name )

Generate fixture data and write to file.

# Arguments

* `p`: input probability
* `alpha`: shape parameter
* `s`: scale parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> p = rand( 1000 );
julia> alpha = rand( 1000 ) * 5.0;
julia> s = rand( 1000 ) * 5.0;
julia> gen( p, alpha, s, "data.json" );
```
"""
function gen( p, alpha, s, name )
	z = Array( Float64, length(p) );
	for i in eachindex(p)
		z[ i ] = quantile( Frechet( alpha[i], s[i] ), p[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("p", p),
		("alpha", alpha),
		("s", s),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Large shape:
p = rand( 1000 );
alpha = rand( 1000 ) * 20.0;
s = rand( 1000 ) * 2.0;
gen( p, alpha, s, "large_shape.json" );

# Large scale:
p = rand( 1000 );
alpha = rand( 1000 ) * 2.0;
s = rand( 1000 ) * 20.0;
gen( p, alpha, s, "large_scale.json" );

# Both large:
p = rand( 1000 );
alpha = rand( 1000 ) * 20;
s = rand( 1000 ) * 20.0;
gen( p, alpha, s, "both_large.json" );
