#!/usr/bin/env julia

import Distributions: entropy, Normal
import JSON

"""
	gen( mu, sigma, name )

Generate fixture data and write to file.

# Arguments

* `mu`: mean parameter
* `sigma`: standard deviation
* `name::AbstractString`: output filename

# Examples

``` julia
julia> mu = rand( 1000 ) * -10.0;
julia> sigma = rand( 1000 ) * 5.0;
julia> gen( mu, sigma, "data.json" );
```
"""
function gen( mu, sigma, name )
	z = Array( Float64, length(mu) );
	for i in eachindex(mu)
		z[ i ] = entropy( Normal( mu[i], sigma[i] ) );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("mu", mu),
		("sigma", sigma),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Generate fixtures:
mu = rand( 10 ) * 2.0 - 4.0;
sigma = rand( 10 ) * 5.0;
gen( mu, sigma, "data.json" );
