#!/usr/bin/env julia

import Distributions: quantile, Poisson
import JSON

"""
	gen( p, lambda, name )

Generate fixture data and write to file.

# Arguments

* `p`: input value
* `lambda`: mean parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> p = rand( 1000 );
julia> lambda = rand( 1000 ) * 50.0;
julia> gen( p, lambda, \"data.json\" );
```
"""
function gen( p, lambda, name )
	z = Array( Float64, length(p) );
	for i in eachindex(p)
		z[ i ] = quantile( Poisson( lambda[i] ), p[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("p", p),
		("lambda", lambda),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Large mean parameter:
p = rand( 1000 );
lambda = rand( 1000 ) * 50.0;
gen( p, lambda, "large_mean.json" );

# Medium mean parameter:
p = rand( 1000 );
lambda = rand( 1000 ) * 20.0;
gen( p, lambda, "medium_mean.json" );

# Small mean parameter:
p = rand( 1000 );
lambda = rand( 1000 ) * 5.0;
gen( p, lambda, "small_mean.json" );
