'use strict';

/**
* Handles general case.
*
* @private
* @param {Function} rgamma - PRNG for gamma distributed numbers
* @param {PositiveNumber} alpha - first shape parameter
* @param {PositiveNumber} beta - second shape parameter
* @returns {Probability} pseudorandom number
*/
function sample( rgamma, alpha, beta ) {
	var x = rgamma( alpha, beta );
	var y = rgamma( alpha, beta );
	return x / ( x + y );
} // end FUNCTION sample()


// EXPORTS //

module.exports = sample;
